// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "./MediaManager.sol";

abstract contract ERC20MediaMethods is ERC20 {
    address internal mediaManagerContractAddress;

    // don't forget to override this method and set the onlyOwner modifier
    function setMediaManagerContractAddress(address _contractAddress) external virtual {
        mediaManagerContractAddress = _contractAddress;
    }

    function tipMedia(uint256 _mediaId, uint256 _amount) external {
        uint256 balance = balanceOf(msg.sender);
        require(balance >= _amount, "Not enough funds for tip the media");

        approve(mediaManagerContractAddress, _amount);

        MediaManager mediaManager = MediaManager(mediaManagerContractAddress);

        mediaManager.tipMedia(msg.sender, _mediaId, _amount);
    }
}
