// SPDX-License-Identifier: MIT
pragma solidity ^0.8.5;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "./FundsManager.sol";

contract Staking is ERC721 {
    using Counters for Counters.Counter;

    uint8 public constant STORAGE_DECIMALS = 18;

    struct Position {
        address stakeToken;
        uint256 stakeAmount;
        uint256 shares;
        uint256 timestamp;
        uint256 totalRewards;
    }

    Counters.Counter private _positionId;

    FundsManager private _fundsManager;

    uint256 public totalStakes;

    //where do tokens for accumulatedRewards come from?
    uint256 public accumulatedRewards;

    uint256 public totalShares;

    mapping(uint256 => Position) private _positions;

    event Staked(
        address indexed staker,
        address stablecoin,
        uint256 amountToken,
        uint256 amountShares,
        uint256 indexed positionId
    );

    event Unstaked(
        uint256 indexed positionId,
        address indexed staker,
        uint256 amountToken,
        address stablecoin
    );

    event Closed(uint256 indexed positionId, address indexed staker);

    //solhint-disable-next-line
    constructor(
        string memory _tokenName,
        string memory _tokenSymbol,
        address fundsManager
    ) ERC721(_tokenName, _tokenSymbol) {
        _fundsManager = FundsManager(fundsManager);
    }

    //TODO: change order of parameters, so that method reads `stakeStablecoin by staker amountToken of stablecoin`
    function stakeStablecoin(
        address staker,
        address stablecoin,
        uint256 amountToken
    ) external {
        require(_fundsManager.isSupported(stablecoin), "Unsupported stablecoin");

        IERC20(stablecoin).transferFrom(msg.sender, address(_fundsManager), amountToken);
        uint256 normalizedAmountToken = _normalizedToStorage(amountToken, stablecoin);
        uint256 shares = _getSharesAmountOut(normalizedAmountToken);

        _mint(staker, stablecoin, amountToken, shares, accumulatedRewards);

        totalShares += shares;
        totalStakes += normalizedAmountToken;

        emit Staked(staker, stablecoin, amountToken, shares, _positionId.current());

        _fundsManager.updateStaking(stablecoin, amountToken);
    }

    function _mint(
        address to,
        address stablecoin,
        uint256 amountToken,
        uint256 amountShares,
        uint256 rewards
    ) internal {
        _positionId.increment();
        uint256 newPositionId = _positionId.current();

        Position memory position = Position({
            stakeToken: stablecoin,
            stakeAmount: amountToken,
            shares: amountShares,
            timestamp: block.timestamp,
            totalRewards: rewards
        });

        _mint(to, newPositionId);

        _positions[newPositionId] = position;
    }

    function getSharesAmountOut(uint256 amountToken, address stablecoin) external view returns (uint256) {
        uint256 normalizedAmountToken = _normalizedToStorage(amountToken, stablecoin);
        return _getSharesAmountOut(normalizedAmountToken);
    }

    /**
     * @dev this method assumes that amountToken is normalized to storage precision using _normalizedToStorage method
     */
    function _getSharesAmountOut(uint256 amountToken) private view returns (uint256) {
        if (totalStakes == 0) {
            return 100;
        }
        return (totalShares * amountToken) / (totalStakes + accumulatedRewards);
    }

    /**
     * @dev normalizes amount of token to storage size. The storage by default stores values with 6 decimals
     *      so when a token with different amount of decimals is used the value is either multiplied
     *      or divided by proper power of 10.
     */
    function _normalizedToStorage(uint256 amount, address token) private view returns (uint256) {
        uint256 decimals = ERC20(token).decimals();
        if (decimals > STORAGE_DECIMALS) {
            return amount / 10**(decimals - STORAGE_DECIMALS);
        } else {
            return amount * 10**(STORAGE_DECIMALS - decimals);
        }
    }

    function unstakeStablecoin(
        uint256 positionId,
        address staker,
        address stablecoin,
        uint256 amountToken
    ) external {
        require(_fundsManager.isSupported(stablecoin), "Unsupported stablecoin");
        require(_positions[positionId].stakeAmount >= amountToken, "Not enough shares to unstake");
        Position storage position = _positions[positionId];

        if (position.stakeAmount - amountToken == 0) {
            _burn(positionId);
            delete _positions[positionId];
            emit Closed(positionId, staker);
        } else {
            uint256 reward = _calculateReward(position);
            _unstake(position, stablecoin, amountToken, reward);
            emit Unstaked(positionId, staker, amountToken, stablecoin);
            _fundsManager.transferStablecoinTo(
                staker,
                stablecoin,
                amountToken + _normalizeRewardToStablecoin(reward, stablecoin)
            );
        }
    }

    function _unstake(
        Position storage position,
        address stablecoin,
        uint256 amountToken,
        uint256 reward
    ) internal {
        uint256 positionStakeNormalized = _normalizedToStorage(position.stakeAmount, stablecoin);
        uint256 amountNormalized = _normalizedToStorage(amountToken, stablecoin);

        uint256 updatedShares = _calculateShares(
            positionStakeNormalized,
            amountNormalized,
            position.shares,
            reward
        );

        totalStakes -= amountNormalized;
        position.stakeAmount -= amountToken;
        totalShares = totalShares + updatedShares - position.shares;
        if (totalShares == 0) {
            totalShares = 100;
            position.shares = 100;
        } else {
            position.shares = updatedShares;
        }
        position.totalRewards = accumulatedRewards;
    }

    function _normalizeRewardToStablecoin(uint256 reward, address stablecoin)
        internal
        view
        returns (uint256)
    {
        uint8 decimals = ERC20(stablecoin).decimals();
        if (decimals == STORAGE_DECIMALS) {
            return reward;
        }

        return
            decimals > STORAGE_DECIMALS
                ? reward * 10**(decimals - STORAGE_DECIMALS)
                : reward / 10**(STORAGE_DECIMALS - decimals);
    }

    /**
     * @dev this method assumes that positionStakeNormalized and amountTokenNormalized are values already normalized to storage precision
     * using _normalizedToStorage method
     */
    function _calculateShares(
        uint256 positionStakeNormalized,
        uint256 amountTokenNormalized,
        uint256 positionShares,
        uint256 reward
    ) private view returns (uint256) {
        uint256 denominator = totalStakes + accumulatedRewards - amountTokenNormalized - reward;
        if (denominator == 0) {
            return 0;
        } else {
            return
                ((totalShares - positionShares) * (positionStakeNormalized - amountTokenNormalized)) /
                denominator;
        }
    }

    function _calculateReward(Position storage position) private view returns (uint256) {
        return ((accumulatedRewards - position.totalRewards) * position.shares) / totalShares;
    }

    function getPosition(uint256 positionId) external view returns (Position memory) {
        return _positions[positionId];
    }

    function depositRewards(uint256 amount, address stablecoin) external {
        require(stablecoin != address(0), "Invalid token");
        IERC20(stablecoin).transferFrom(msg.sender, address(_fundsManager), amount);
        _fundsManager.updateStaking(stablecoin, amount);
        accumulatedRewards += _normalizedToStorage(amount, stablecoin);
    }

    function getClaimableRewards(uint256 positionId) external view returns (uint256) {
        return _calculateReward(_positions[positionId]);
    }

    function getSharesAfterUnstaking(uint256 positionId, uint256 amount) external view returns (uint256) {
        Position storage position = _positions[positionId];
        uint256 positionStakeNormalized = _normalizedToStorage(position.stakeAmount, position.stakeToken);
        uint256 amountNormalized = _normalizedToStorage(amount, position.stakeToken);
        uint256 reward = _calculateReward(position);
        return _calculateShares(positionStakeNormalized, amountNormalized, position.shares, reward);
    }
}

