// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "./Structs.sol";

/**
 * @title verified artists addresses contract
 * @author Eric Nordelo
 */
contract VerifiedArtists is AccessControl {
    bytes32 public constant VERIFIER_ROLE = keccak256("VERIFIER_ROLE");

    mapping(bytes32 => Artist) public verifiedArtists;

    event VerifyArtist(address indexed account, bytes32 id);

    constructor() {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);

        // Default admin can add verifiers
        _setRoleAdmin(VERIFIER_ROLE, DEFAULT_ADMIN_ROLE);
    }

    /**
     * @dev allows admins to verify an artist
     * @param _identifier the string identifying the artist (name and dni, or something like that)
     * @param _account the verified artist account address
     */
    function verify(string calldata _identifier, address _account) external onlyRole(VERIFIER_ROLE) {
        bytes32 id = keccak256(abi.encodePacked(_identifier));

        require(verifiedArtists[id].account == address(0), "Artist already verified");

        verifiedArtists[id] = Artist({account: _account, registeredSongs: 0});

        emit VerifyArtist(_account, id);
    }

    /**
     * @notice allows an artist to update her/his account address
     * @param _artistId the bytes32 id of the artist (keccak256 of the identifier string)
     * @param _newAccount the new account address
     */
    function changeAccount(bytes32 _artistId, address _newAccount) external {
        require(verifiedArtists[_artistId].account == msg.sender, "Can't update the account");
        verifiedArtists[_artistId].account = _newAccount;
    }
}

