pragma solidity ^0.8.4;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/proxy/utils/Initializable.sol";

//TODO: check in metamask that modified transfer function doesn't cause problems
contract SocialERC20 is ERC20, AccessControl, Initializable {
    uint256 public constant DECIMALS = 10**18;
    bytes32 public constant OWNER_ROLE = keccak256("OWNER_ROLE");

    struct VestingEntry {
        uint256 registrationTimestamp;
        uint256 vestingEndsTimestamp;
        uint256 vestingPeriod;
        uint256 vestedAmount;
        uint256 claimedAmount;
    }

    uint256 public totalCap;

    uint256 public availableReserves;

    uint256 public accumulatedTaxes;

    string private _proxiedName;
    string private _proxiedSymbol;

    uint256 private _tax;

    address[] private _airdropAddresses;

    mapping(address => uint256) private _airdropAmounts;

    mapping(address => VestingEntry[]) public vesting;

    address private _taxTo;

    string public logoUrl;

    event TokensClaimed(address caller, address claimer, uint256 amount, uint256 claimPeriod);

    // solhint-disable-next-line
    constructor() ERC20("", "") {
    }

    function initialize(
        address owner,
        string memory name_,
        string memory symbol_,
        uint256 _totalCap,
        uint256 initialMint,
        uint256 tax,
        address taxTo,
        string calldata _logoUrl
    ) external initializer {
        require(owner != address(0), "Invalid address of owner");
        require(_totalCap > 0, "Total cap must be greater than zero");
        require(_totalCap >= initialMint, "Total cap must be greater or equal to initially minted ");
        require(tax <= DECIMALS, "Cannot tax more than 100%");
        require(taxTo != address(0), "Invalid address for revenue manager");
        _proxiedName = name_;
        _proxiedSymbol = symbol_;
        _tax = tax;
        _taxTo = taxTo;
        totalCap = _totalCap;
        availableReserves = _totalCap - initialMint;
        logoUrl = _logoUrl;
        _mint(owner, initialMint);
        _setupRole(OWNER_ROLE, owner);
    }

    function name() public view virtual override returns (string memory) {
        return _proxiedName;
    }

    function symbol() public view virtual override returns (string memory) {
        return _proxiedSymbol;
    }

    function transfer(address recipient, uint256 amount) public virtual override returns (bool) {
        uint256 taxedAmount = _taxTransfer(msg.sender, amount);
        return super.transfer(recipient, taxedAmount);
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public virtual override returns (bool) {
        uint256 taxedAmount = _taxTransfer(sender, amount);
        return super.transferFrom(sender, recipient, taxedAmount);
    }

    function _taxTransfer(address sender, uint256 amount) internal returns (uint256){
        uint256 taxAmount = (amount * _tax) / DECIMALS;
        accumulatedTaxes += taxAmount;
        _transfer(sender, _taxTo, taxAmount);
        return amount - taxAmount;
    }

    function airdropTokens(address[] memory addresses, uint256[] memory amounts) external onlyRole(OWNER_ROLE) {
        require(addresses.length == amounts.length, "Number of addresses is not equal to number of amounts");
        for (uint256 i = 0; i < addresses.length; i++) {
            require(availableReserves >= amounts[i], "Insufficient reserves for airdrop");
            availableReserves -= amounts[i];
            _mint(addresses[i], amounts[i]);
        }
    }

    function allocateTokens(
        address[] memory addresses,
        uint256[] memory amounts,
        uint256[] memory immediate,
        uint256[] memory dates
    ) external onlyRole(OWNER_ROLE) {
        require(addresses.length == amounts.length, "Number of addresses is not equal to number of amounts");
        require(dates.length == amounts.length, "Number of periods is not equal to number of amounts");
        for (uint256 i = 0; i < addresses.length; i++) {
            require(availableReserves >= amounts[i], "Insufficient reserves for vesting");
            availableReserves -= amounts[i];
            vesting[addresses[i]].push(VestingEntry({
                // solhint-disable-next-line
                registrationTimestamp: block.timestamp,
                vestingEndsTimestamp: dates[i],
                // solhint-disable-next-line
                vestingPeriod: dates[i] - block.timestamp,
                vestedAmount: amounts[i] - immediate[i],
                claimedAmount: 0
            }));
            if(immediate[i] > 0) {
                emit TokensClaimed(msg.sender, addresses[i], immediate[i], 0);
                _mint(addresses[i], immediate[i]);
            }
        }
    }

    function claimTokens(address to) external {
        require(to != address(0), "Invalid address to send tokens to");
        require(vesting[msg.sender].length > 0, "No vested tokens for caller");
        for(uint i = 0;i < vesting[msg.sender].length;i++) {
            VestingEntry storage entry = vesting[msg.sender][i];
            // solhint-disable-next-line
            uint256 claimPeriod = (block.timestamp - entry.registrationTimestamp) > entry.vestingPeriod ? entry.vestingPeriod : block.timestamp - entry.registrationTimestamp;
            uint256 amount = claimPeriod * entry.vestedAmount / entry.vestingPeriod - entry.claimedAmount;
            entry.claimedAmount += amount;
            emit TokensClaimed(msg.sender, to, amount, claimPeriod);
            _mint(to, amount);
        }
    }

    function setTaxTo(address newTaxTo) external onlyRole(OWNER_ROLE) {
        require(newTaxTo != address(0), "Invalid taxTo address");
        _taxTo = newTaxTo;
    }
}
