pragma solidity ^0.8.4;

import "@openzeppelin/contracts/proxy/Clones.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "./SocialERC20.sol";

contract SocialTokenDeployer {
    bytes32 public constant OWNER_ROLE = keccak256("OWNER_ROLE");

    address private immutable _tokenBlueprint;

    event TokenSpawned(
        address token,
        address owner,
        address spawner,
        uint256 totalCap,
        uint256 initial,
        uint256 reserved,
        string logoUrl
    );

    constructor(address tokenBlueprint) {
        require(tokenBlueprint != address(0), "Invalid address token blueprint");
        _tokenBlueprint = tokenBlueprint;
    }

    function spawn(
        string memory name,
        string memory symbol,
        uint256 totalCap,
        uint256 initialMint,
        uint256 tax,
        address taxTo,
        string calldata logoUrl
    ) external {
        address tokenAddress = Clones.clone(_tokenBlueprint);
        SocialERC20(tokenAddress).initialize(msg.sender, name, symbol, totalCap, initialMint, tax, taxTo, logoUrl);
        emit TokenSpawned(
            tokenAddress,
            msg.sender,
            msg.sender,
            totalCap,
            initialMint,
            totalCap - initialMint,
            logoUrl
        );
    }

    function spawnFor(
        address owner,
        string memory name,
        string memory symbol,
        uint256 totalCap,
        uint256 initialMint,
        uint256 tax,
        address taxTo,
        string calldata logoUrl
    ) external {
        address tokenAddress = Clones.clone(_tokenBlueprint);
        SocialERC20(tokenAddress).initialize(owner, name, symbol, totalCap, initialMint, tax, taxTo, logoUrl);
        emit TokenSpawned(tokenAddress, owner, msg.sender, totalCap, initialMint, totalCap - initialMint, logoUrl);
    }
}
