            _symbol.tradersNetVolume -= position.volume;
            _symbol.tradersNetCost -= position.cost;
            netEquity +=
                (((position.volume * _symbol.price) / ONE) * _protocolParameters.futuresMultiplier()) /
                ONE -
                position.cost;
        }

